import React from 'react';
import {useTranslation} from 'react-i18next';

import {StyleSheet, ScrollView, View} from 'react-native';
import {Text, Button} from 'src/components';
import OpacityView from 'src/containers/OpacityView';
import FooterOrderInfo from './FooterOrderInfo';
import AvatarOrderInfo from './AvatarOrderInfo';

import currencyFormatter from 'src/utils/currency-formatter';
import {orange, blue} from 'src/components/config/colors';
import {margin, padding, borderRadius} from 'src/components/config/spacing';

const OrderInfo = props => {
  const {totals, currency, processCheckout, checkoutLoading} = props;
  const {t} = useTranslation();
  return (
    <View style={styles.container}>
      <ScrollView style={styles.scroll} showsVerticalScrollIndicator={false}>
        <View style={styles.viewContent}>
          <Text medium h2 style={styles.textTitle}>
            {t('cart:text_payment_confirm')}
          </Text>
          <AvatarOrderInfo />
          <Text medium h4 style={styles.textId}>
            {t('cart:text_payment_id', {id: '8809'})}
          </Text>
          <OpacityView bgColor={blue} opacity={0.08} style={styles.viewMessage}>
            <Text style={styles.textMessage}>{'6867687'}</Text>
          </OpacityView>
        </View>
      </ScrollView>
      <FooterOrderInfo
        shipping={currencyFormatter(totals?.shipping_total, currency)}
        discount={currencyFormatter(totals?.discount_total, currency)}
        tax={currencyFormatter(totals?.total_tax, currency)}
        total={currencyFormatter(totals?.total, currency)}
        onPressConfirm={processCheckout}
        style={styles.footer}
        loading={checkoutLoading}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  scroll: {
    flexGrow: 1,
  },
  viewContent: {
    alignItems: 'center',
    paddingHorizontal: padding.big,
  },
  textTitle: {
    marginBottom: margin.big,
  },
  textId: {
    color: orange,
    marginTop: margin.large * 2,
    marginBottom: margin.big,
  },
  viewMessage: {
    width: '100%',
    borderRadius: borderRadius.large,
    marginBottom: 2 * margin.big,
  },
  textMessage: {
    marginVertical: margin.large + 2,
    marginHorizontal: margin.big,
    textAlign: 'center',
    color: blue,
  },
  footer: {
    paddingHorizontal: padding.big,
    marginBottom: margin.big,
  },
});

export default OrderInfo;
