import React from 'react';
import {useTranslation} from 'react-i18next';

import {StyleSheet} from 'react-native';
import {Text, ListItem} from 'src/components';

import {grey4} from 'src/components/config/colors';
import {icon, titleProps} from './config';
import {margin, padding} from 'src/components/config/spacing';
import {mainStack} from 'src/config/navigator';
import {connect} from 'react-redux';
import {signOut} from 'src/modules/auth/actions';

const SettingMe = ({
  isLogin,
  phonenumber,
  clickPage,
  goPhone,
  handleSignOut,
}) => {
  const {t} = useTranslation();

  return (
    <>
      <Text medium style={styles.title}>
        {t('profile:text_title_setting')}
      </Text>
      <ListItem
        leftIcon={icon(0)}
        title={t('common:text_setting')}
        type="underline"
        titleProps={titleProps}
        pad={padding.large}
        chevron
        onPress={() => clickPage(mainStack.setting)}
      />
      <ListItem
        leftIcon={icon(1)}
        title={t('common:text_help_info')}
        type="underline"
        titleProps={titleProps}
        chevron
        pad={padding.large}
        onPress={() => clickPage(mainStack.help)}
      />
      <ListItem
        leftIcon={icon(2)}
        title={t('profile:text_hotline')}
        rightElement={
          <Text colorThird style={styles.phone}>
            {phonenumber}
          </Text>
        }
        type="underline"
        titleProps={titleProps}
        pad={padding.large}
        containerStyle={!isLogin && styles.itemEnd}
        onPress={() => goPhone(`tel:${phonenumber}`)}
      />
      {isLogin && (
        <ListItem
          leftIcon={icon(3)}
          title={t('profile:text_signout')}
          type="underline"
          titleProps={titleProps}
          pad={padding.large}
          containerStyle={styles.itemEnd}
          onPress={handleSignOut}
        />
      )}
    </>
  );
};

const styles = StyleSheet.create({
  title: {
    color: grey4,
    marginTop: margin.big + 4,
    marginBottom: margin.small,
  },
  phone: {
    marginHorizontal: margin.small / 2,
  },
  itemEnd: {
    borderBottomWidth: 0,
  },
});

SettingMe.defaultProps = {
  isLogin: false,
  phonenumber: '',
  clickPage: () => {},
  goPhone: () => {},
};
const mapDispatchToProps = {
  handleSignOut: signOut,
};
export default connect(null, mapDispatchToProps)(SettingMe);
