import React from 'react';
import {StyleSheet, View} from 'react-native';
import {Icon, withTheme} from 'src/components';
import InputBasic from './InputBasic';
import ViewLabel, {MIN_HEIGHT} from '../ViewLabel';
import {grey4} from 'src/components/config/colors';
import {padding, margin} from 'src/components/config/spacing';

class Input extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isSecure: props.secureTextEntry,
      isHeading: props.value || props.defaultValue,
    };
    this.input = React.createRef();
  }

  componentDidUpdate(prevProps) {
    if (prevProps.value !== this.props.value) {
      this.updateHeading(this.props.value);
    }
  }
  updateHeading = value => {
    this.setState({
      isHeading: value,
    });
  };

  handleFocus = data => {
    this.updateHeading(true);
    if (this.props.onFocus) {
      this.props.onFocus(data);
    }
  };
  onChange = value => {
    this.setState(
      {
        value,
      },
      () => {
        if (this.props.onChangeText) {
          this.props.onChangeText(value);
        }
      },
    );
  };
  handleBlur = data => {
    const {value} = this.props;
    this.updateHeading(
      value || (this.input.current && this.input.current._lastNativeText),
    );
    if (this.props.onBlur) {
      this.props.onBlur(data);
    }
  };

  render() {
    const {label, error, secureTextEntry, theme, style, multiline, ...rest} =
      this.props;
    const {isSecure, isHeading} = this.state;
    return (
      <ViewLabel label={label} error={error} isHeading={isHeading}>
        <View style={styles.viewInput}>
          <InputBasic
            {...rest}
            inputRef={this.input}
            testID="RN-text-input"
            onBlur={this.handleBlur}
            onFocus={this.handleFocus}
            secureTextEntry={isSecure}
            multiline={multiline}
            style={[
              styles.input,
              !multiline && {
                height: MIN_HEIGHT,
              },
              style && style,
            ]}
          />
          {secureTextEntry && (
            <Icon
              name={isSecure ? 'eye' : 'eye-off'}
              color={isSecure ? grey4 : theme.colors.primary}
              size={15}
              containerStyle={styles.viewIcon}
              iconStyle={styles.icon}
              underlayColor="transparent"
              onPress={() =>
                this.setState({
                  isSecure: !isSecure,
                })
              }
            />
          )}
        </View>
      </ViewLabel>
    );
  }
}

const styles = StyleSheet.create({
  viewInput: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  input: {
    flex: 1,
    paddingHorizontal: padding.large,
  },
  viewIcon: {
    marginRight: margin.large,
  },
  icon: {
    paddingVertical: padding.base,
  },
});

export default withTheme(Input);
