// @flow
import React from 'react';
import {StyleSheet, View} from 'react-native';
import {black} from 'src/components/config/colors';
type Props = {
  opacity?: number,
  bgColor?: string,
  children: React.Node,
};

const OpacityView = (props: Props) => {
  const {opacity, bgColor, children, style} = props;
  return (
    <View style={[styles.container, style && style, styles.viewContainer]}>
      <View
        style={[
          styles.boxOpacity,
          {
            opacity: opacity,
            backgroundColor: bgColor,
          },
        ]}
      />
      {children}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    overflow: 'hidden',
  },
  viewContainer: {
    backgroundColor: 'transparent',
  },
  boxOpacity: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
  },
});

OpacityView.defaultProps = {
  opacity: 0.5,
  bgColor: black,
};

export default OpacityView;
